import React from 'react';
import MUIDataTable from "mui-datatables";
import { IconButton } from '@material-ui/core';
import DeleteIcon from '@material-ui/icons/Delete';
import './App.css';

const data = [{
  "id": 1,
  "name": "Rock",
  "email": "rock@demo.com",
  "designation": "Software Developer",
},
{
  "id": 2,
  "name": "Jone",
  "email": "jone@demo.com",
  "designation": "Java Developer",
},
{
  "id": 3,
  "name": "Tom",
  "email": "Tom@demo.com",
  "designation": "Frontend Developer",
},
{
  "id": 4,
  "name": "Robert",
  "email": "robert@demo.com",
  "designation": "Backend Developer",
},
{
  "id": 5,
  "name": "Zac",
  "email": "zac@demo.com",
  "designation": "Ios Developer",
},
{
  "id": 6,
  "name": "David",
  "email": "david@demo.com",
  "designation": "Android Developer",
}]

const columns = [
  {
    name: "name",
    label: "Name",
    options: {
      filter: true,
      sort: true,
    }
  },
  {
    name: "email",
    label: "Email",
    options: {
      filter: true,
      sort: false,
    }
  },
  {
    name: "designation",
    label: "Designation",
    options: {
      filter: true,
      sort: false,
    }
  }];

const COL_NAME = { name: "name", options: { sort: true, filter: true } }
const COL_EMAIL = { name: "email", options: { sort: true, filter: true } }
const COL_DESIGNATION = { name: "designation", options: { sort: true, filter: true } }
const COL_ACTIONS = {
  name: "action", options: {
    sort: true, filter: true,
    customBodyRender: () => {
      return (
        <React.Fragment>
          <IconButton ><DeleteIcon /></IconButton>
        </React.Fragment>
      );
    }
  }
}

const tableColumns = () => [COL_NAME, COL_EMAIL, COL_DESIGNATION, COL_ACTIONS]


class App extends React.Component {
  render() {
    return (
      <React.Fragment>
        <div className="App">
          <h1>MUI Datatable with react</h1>
          <MUIDataTable
            title={""}
            data={data}
            columns={tableColumns()}
          />
        </div>
      </React.Fragment> 
    );
  }
}

export default App;
