import React from 'react';
import MUIDataTable from "mui-datatables";
import { IconButton } from '@material-ui/core';
import DeleteIcon from '@material-ui/icons/Delete';
import './App.css';

const data = [{
  "id": 1,
  "name": "Rock",
  "email": "rock@demo.com",
  "designation": "Software Developer",
},
{
  "id": 2,
  "name": "Jone",
  "email": "jone@demo.com",
  "designation": "Java Developer",
},
{
  "id": 3,
  "name": "Tom",
  "email": "Tom@demo.com",
  "designation": "Frontend Developer",
},
{
  "id": 4,
  "name": "Robert",
  "email": "robert@demo.com",
  "designation": "Backend Developer",
},
{
  "id": 5,
  "name": "Zac",
  "email": "zac@demo.com",
  "designation": "Ios Developer",
},
{
  "id": 6,
  "name": "David",
  "email": "david@demo.com",
  "designation": "Android Developer",
}]
const COL_ID = { name: "id", label: "ID", options: { sort: true, filter: true } }
const COL_NAME = { name: "name", label: "Name", options: { sort: true, filter: true } }
const COL_EMAIL = { name: "email", label: "Email", options: { sort: true, filter: true } }
const COL_DESIGNATION = { name: "designation", label: "Designation", options: { sort: true, filter: true } }
const COL_ACTIONS = {
  name: "action", label: "Action", options: {
    sort: true, filter: true,
    customBodyRender: () => {
      return (
        <React.Fragment>
          <IconButton ><DeleteIcon /></IconButton>
        </React.Fragment>
      );
    }
  }
}

const tableColumns = () => [COL_ID, COL_NAME, COL_EMAIL, COL_DESIGNATION, COL_ACTIONS]


class App extends React.Component {
  render() {
    const displayData = this.state.users.map((row, index) => [row.email, row.groups.map(u => u.name).join(', '), { id: row.id, email: row.email, status: row.status, isAdmin: row.is_admin ? 'Yes' : 'No', isApiConsumer: row.is_api_consumer ? 'Yes' : 'No' }]);
    console.log(displayData)
    return (
      <React.Fragment>
        <div className="App">
          <h1>MUI Datatable with react</h1>
          <MUIDataTable
            title={""}
            data={displayData}
            columns={tableColumns()}
          />
        </div>
      </React.Fragment> 
    );
  }
}

export default App;
