//     sort: true, filter: true,
//     customBodyRender: (data) => {
//       console.log(data);
//       return (
//         <React.Fragment>
//           <IconButton ><DeleteIcon /></IconButton>
//         </React.Fragment>
//       );
//     }
//   }
// }

const tableColumns = () => [COL_ID, COL_NAME, COL_EMAIL, COL_DESIGNATION]


class App extends React.Component {
  render() {
    return (
      <React.Fragment>
        <div className="App">
          <h1>MUI Datatable with react</h1>
          <MUIDataTable
            title={""}
            data={data}
            columns={tableColumns()}
            actions={[
              {
                icon: 'delete',
                tooltip: 'Delete',
                onClick: (event, rowData ) => {
                  console.log(rowData);
                }
              }
            ]}
          />
        </div>
      </React.Fragment> 
    );
  }
}

export default App;
